(function($){$.fn.SharePointFormSubmit=function(arr){var frmMethod=arr['frmMethod'];var frmAction=arr['frmAction'];var element=arr['element'];frmTarget=(arr['frmTarget']===undefined)?'_self':arr['frmTarget'];frmID=(arr['frmID']===undefined)?'':arr['frmID'];frmName=(arr['frmName']===undefined)?'':arr['frmName'];debug=((arr['debug']===undefined)||(arr['debug']==false))?'none':'block';var f=$('<form></form>');f.attr('method',frmMethod);f.attr('action',frmAction);f.attr('target',frmTarget);f.attr('id',frmID);f.attr('name',frmName);f.css('display',debug);$(element).find('select, textarea, :input:not(:checkbox)').each(function(){e=$('<input>');e.attr('type',$(this).attr('type'));e.attr('id',$(this).attr('id'));e.attr('name',$(this).attr('name'));e.attr('value',$(this).val());e.attr('checked',$(this).attr('checked'));e.attr('multiple',$(this).attr('multiple'));f.append(e);});$(element).find('input:checkbox').each(function(){if($(this).attr('checked'))
{chkboxChecked='checked';}else{chkboxChecked='';};e='<input type="checkbox" id="'+$(this).attr("id")+'" name="'+$(this).attr("name")+'" checked="'+chkboxChecked+'">';f.append(e);});if(arr['sbmCustom']){sbmt=$('<submit>');sbmt.attr('type','submit');sbmt.attr('id',arr['sbmID']);sbmt.attr('name',arr['sbmName']);sbmt.attr('value',arr['sbmValue']);f.append(sbmt);}
var h=$('body').append(f);f.submit();};})(jQuery);